sysauto_script.script:
// Get today's time and date and add 24 hours (1 day from current date and time)
	//var rightNow = new GlideDateTime();
		//rightNow.addDaysLocalTime(+1);

	var rightNowA = new GlideDateTime();
	var rightNowB = new GlideDateTime();
		rightNowB.addDaysLocalTime(1);

	// Query the database for NeedIt Task records with Due date field values of today plus 24 hours
	// from the current date and time.  Only return NeedIt Task records that do not have
	// a State field value of Closed Complete. JB - changed vaiable name from todaydue to dueSoon

	var dueSoonNITasks = new GlideRecord('x_58872_needit_needit_task');

		dueSoonNITasks.addQuery('due_date','>=',rightNowA); //&& ('due_date','<=',rightNowB));
		dueSoonNITasks.addQuery('due_date','<=',rightNowB);
		dueSoonNITasks.addQuery('state','!=',3);

//example from the lesson   ('due_date','<',gs.hoursAgo(-24));

	dueSoonNITasks.query();
	// Write a log message for each due NeedIt Task Record
	while(dueSoonNITasks.next()){
		gs.info("Due today NeedIt Task records = " + dueSoonNITasks.number);
		gs.info("rightNowA variable value = " + rightNowA);
		gs.info("rightNowB variable value = " + rightNowB);
		gs.eventQueue('x_58872_needit.dueSoonNeedItTask',dueSoonNITasks,dueSoonNITasks.number,gs.getUserName());
	}



//var gdt = new GlideDateTime("2011-08-31 08:00:00");
